mapper.update:
UPDATE express_address
update_time = #{address.updateTime}, is_default = #{address.isDefault}, detail_address = #{address.detailAddress}, city_area = #{address.cityArea}, phone_number = #{address.phoneNumber}, name = #{address.name}
id = #{address.id}

mapper.select:
SELECT id, create_time, update_time, is_default, detail_address, city_area, phone_number, name
FROM express_address
WHERE id = #{id}
